    private void ApplyMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ApplyMouseClicked
        boolean a = false;
        boolean p = false;
        String check1 = new String(ApplyP.getPassword());
        String check2 = new String(ConfirmP.getPassword());
                
        if( ApplyA.getText().equals("") ) {
            JOptionPane.showMessageDialog(this, "請輸入帳號", "", JOptionPane.ERROR_MESSAGE);
        } else if( ApplyA.getText().contains(" ") ) {
            JOptionPane.showMessageDialog(this, "帳號請不要有空白", "", JOptionPane.ERROR_MESSAGE);
            ApplyA.setText("");
        } else {
            ApplyAccount  = ApplyA.getText();
            a = true;
        }

        if(check1.equals("") ) {
            JOptionPane.showMessageDialog(this, "請輸入密碼", "", JOptionPane.ERROR_MESSAGE);
        } else if( check1.contains(" ") ) {
            JOptionPane.showMessageDialog(this, "密碼請不要有空白", "", JOptionPane.ERROR_MESSAGE);
            ApplyP.setText("");
        } else if (check1.equals(check2)) {
            ApplyPassword = new String(ApplyP.getPassword());
            p = true;
        } else {
            JOptionPane.showMessageDialog(this, "兩次密碼輸入不相同,請再次確認密碼","", JOptionPane.ERROR_MESSAGE);
        }

        if (a ==true && p == true){
             setVisible(false);
             if(NewMain.NWKSER.loginas(ApplyAccount, ApplyPassword, true)){

             }
             else{
                 setVisible(true);
                 JOptionPane.showMessageDialog(this, "連線失敗,請檢察網路線或路由器", "", JOptionPane.ERROR_MESSAGE);
             }
             
        }

    }//GEN-LAST:event_ApplyMouseClicked

