    public void RegisterFailed(){
        setVisible(true);
        JOptionPane.showMessageDialog(this, "帳號已被註冊,請重新輸入","", JOptionPane.ERROR_MESSAGE);
        ApplyA.setText("");
        ApplyP.setText("");
        ConfirmP.setText("");
    }

    public void RegisterSuccess(){
        JOptionPane.showMessageDialog(this, "註冊成功,已登入", "", JOptionPane.PLAIN_MESSAGE);
    }

